import { Component, ElementRef, NgZone, OnInit, OnDestroy } from '@angular/core';
import { gsap } from 'gsap';

@Component({
  selector: 'app-loading',
  templateUrl: './app-loading.component.html',
  styleUrl: './app-loading.component.scss'
})
export class AppLoadingComponent implements OnInit, OnDestroy {
  counter: number = 0;
  loadingBarTimeline!: gsap.core.Timeline;

  constructor(private elementRef: ElementRef, private ngZone: NgZone) { }

  ngOnInit(): void {
    this.ngZone.runOutsideAngular(() => {
      this.initLoadingBarAnimation();
    });
  }

  ngOnDestroy(): void {
    // Ensure to kill the animation when the component is destroyed
    if (this.loadingBarTimeline) {
      this.loadingBarTimeline.kill();
    }
  }

  private initLoadingBarAnimation(): void {
    const loadingBarElement = this.elementRef.nativeElement.querySelector('.loading-bar');
    const counterElement = this.elementRef.nativeElement.querySelector('.counter');

    this.loadingBarTimeline = gsap.timeline({ onUpdate: this.updateCounter.bind(this) });

    this.loadingBarTimeline
      .fromTo(loadingBarElement, { width: '0%' }, { width: '100%', duration: 2, ease: 'power2.inOut' })
      .to(counterElement, { opacity: 1, duration: 0.5 }, '-=0.5');
  }

  private updateCounter(): void {
    this.counter = Math.round((this.loadingBarTimeline.progress() * 100));
  }
}
