import { Component, HostListener, OnInit } from '@angular/core';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { ScrollSmoother } from 'gsap/ScrollSmoother';

gsap.registerPlugin(ScrollTrigger, ScrollSmoother);

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit {
  // isLoading: boolean = true;

  ngOnInit() {
    // // Simulate an asynchronous operation (e.g., HTTP request)
    // setTimeout(() => {
    //   // Set isLoading to false to hide the loading component
    //   this.isLoading = false;
    // }, 2000); // Adjust the time as needed


    gsap.to(window, {
      scrollTo: 0,
      duration: 1,
      ease: 'power2.out',
      scrollTrigger: {
        trigger: '.main-container', // Adjust the trigger element as needed
        start: 'top top',
        end: 'bottom bottom',
        scrub: 1, // Adjust scrub value for smoother animation
      },
    });
  }
  @HostListener('window:scroll', ['$event'])
  onScroll(event: Event): void {
    // You can add additional logic here if needed
  }

}
