import { Component, OnInit, OnDestroy } from '@angular/core';
import { gsap } from 'gsap';
import { ScrollSmoother } from 'gsap/ScrollSmoother';


gsap.registerPlugin(ScrollSmoother);


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit, OnDestroy {
  private smoother!: ScrollSmoother;

  isLoading: boolean = true;

  ngOnInit() {
    setTimeout(() => {
      this.isLoading = false;
    }, 2000);
    // Initialize ScrollSmoother when the component mounts
    this.smoother = ScrollSmoother.create({
      smooth: 2, // Smoothness factor (higher means more smoothing)
      effects: true, // Enable effects tied to scroll position
      normalizeScroll: true
      wrapper: document.querySelector(".main") // Target the div with class 'main'
      // Additional configuration properties...
    });
  }
  ngOnDestroy(): void {
    if (this.smoother) {
      this.smoother.kill();
    }
  }
}
