    //     this.loadingPercentage += 1;
    //     // Update the counter element
    //     gsap.to('.counter', {
    //       innerHTML: this.round(this.loadingPercentage) + '%',
    //       duration: 2.5, // Adjust the duration as needed
    //       ease: 'power2.inOut'
    //     });
    //   }
    // }, /* Set your interval duration here, e.g., 1000 for 1 second */);
  }

  ngAfterViewInit() {
  }
  round(value: number): string {
    return value.toFixed(0);
  }
}
