import { AfterViewInit, Component, OnInit } from '@angular/core';
import { ScrollSmoother } from 'gsap/ScrollSmoother';
import gsap from 'gsap';

gsap.registerPlugin(ScrollSmoother);

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit, AfterViewInit {
  // isLoading: boolean = true;

  ngOnInit() {
    // Simulate an asynchronous operation (e.g., HTTP request)
    // setTimeout(() => {
    //   // Set isLoading to false to hide the loading component
    //   this.isLoading = false;
    // }, 2000); // Adjust the time as needed
    ScrollSmoother.create({
      wrapper: ".main",
      content: ".main-container",
      smooth: 2,
      effects: true,
    });

    // Create a ScrollSmoother instance
    const smoother = ScrollSmoother.create({
      wrapper: ".main",
      content: ".main-container",
      smooth: 1, // Higher values make the smooth scrolling more pronounced
      effects: true, // Enable effects like parallax
      // any other configuration options...
    });

  }

  ngAfterViewInit() {

    // ScrollSmoother.create({
    //   wrapper: ".main",
    //   content: ".main-container",
    //   smooth: 2,
    //   effects: true,
    // });
  }

}
