import { Component, ElementRef, NgZone, OnInit, OnDestroy } from '@angular/core';
import { gsap } from 'gsap';

@Component({
  selector: 'app-loading',
  templateUrl: './app-loading.component.html',
  styleUrl: './app-loading.component.scss'
})
export class AppLoadingComponent implements OnInit, OnDestroy {
  counter: number = 0;
  loadingBarTimeline: gsap.core.Timeline | undefined;

  constructor(private elementRef: ElementRef, private ngZone: NgZone) {}

  ngOnInit(): void {
    this.ngZone.runOutsideAngular(() => {
      this.initLoadingBarAnimation();
    });
  }

  ngOnDestroy(): void {
    // Ensure to kill the animation when the component is destroyed
    if (this.loadingBarTimeline) {
      this.loadingBarTimeline.kill();
    }
  }


}
