// import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import gsap from 'gsap';

@Component({
  selector: 'app-loading',
  templateUrl: './app-loading.component.html',
  styleUrl: './app-loading.component.scss'
})
export class AppLoadingComponent implements OnInit {

  isLoading = false;

  ngOnInit() {
    // Simulate a delay to test the loading progress bar
    this.showLoading();
    setTimeout(() => {
      this.hideLoading();
    }, 9000);
  }

  showLoading() {
    this.isLoading = true;
    gsap.to('.loading-bar', { width: '100%', duration: 0.5, ease: 'power2.inOut' });
  }

  hideLoading() {
    this.isLoading = false;
    gsap.to('.loading-bar', { width: '0%', duration: 0.5, ease: 'power2.inOut' });
  }
}
