import { AfterViewInit, Component, ElementRef, ViewChild } from '@angular/core';

@Component({
  selector: 'inquire',
  templateUrl: './inquire.component.html',
  styleUrls: ['./inquire.component.scss']
})
export class InquireComponent implements AfterViewInit {
  @ViewChild('inquireContentAnimation', { static: true }) inquireContentAnimation!: ElementRef;


  ngAfterViewInit() {
    this.playTopRightItemAnimation();
  }


  playTopRightItemAnimation() {
    // Obtain the reference to the second element
    const inquireContent = this.inquireContentAnimation.nativeElement;

    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: inquireContent,
        start: "top center",
        end: "bottom center",
        toggleActions: "play none none none"
      },
      defaults: { duration: 0.5 },
    });

    // Define the animation for the second element (top-right-item)
    tl
      .from(inquireContent.querySelector('.section-title .title-label'), { opacity: 0 })
      .from(inquireContent.querySelectorAll('.section-title .title'), { opacity: 0 }, '-=0.2')
      .from(inquireContent.querySelector(' .line img'), { width: 0, opacity: 0 }, '-=0.3')
      .from(inquireContent.querySelector(' .img-sm'), { scale: 0, opacity: 0 }, '-=0.4')
      .from(inquireContent.querySelectorAll(' .para'), { opacity: 0, duration: 0.5 })
      .from(inquireContent.querySelector(' .btn-trans'), { opacity: 0, ease: 'elastic.out(1, 0.3)' })
      .from(inquireContent.querySelector('.big-img'), { x: 100, opacity: 0, duration: 0.6 })
      .from(inquireContent.querySelector('.list-content .title'), { opacity: 0, stagger: 0.2 })
      .from(inquireContent.querySelectorAll('.list-content li'), { opacity: 0, stagger: 0.2 });
  }
}
